const rowHeaderStyle = {
  borderBottom: "1px solid #eee",
  padding: "2px 4px",
  textAlign: "left",
  background: "#fafafa",
  minWidth: 110
};

const tdStyle = {
  borderBottom: "1px solid #eee",
