      return t.passengerGasoline;
    case "passengerDiesel":
      return t.passengerDiesel;
    case "busSmall":
      return t.busSmall;
    case "busLarge":
      return t.busLarge;
    case "truckSmall":
      return t.truckSmall;
    case "truckMedium":
      return t.truckMedium;
    case "truckLarge":
