const cellInputStyle = {
  width: "100%",
  padding: "2px 4px",
  fontSize: 12,
  boxSizing: "border-box",
  borderRadius: 3,
  border: "1px solid #ccc"
};

const hintText = {
  marginTop: 8,
  fontSize: 11,
  color: "#555"
};


