// src/modules/designConditions/DesignConditionsForm.jsx
import React, { useState } from "react";
import {
  computeDirectionStats,
  estimateTrafficVolume,
  SPEED_CAPACITY_TABLE
} from "./calculations";

const EMPTY_SEGMENT = {
  slopePercent: "",
  lengthM: "",
  avgElevationM: "",
  laneCount: ""
};

const SEGMENT_COUNT_PER_DIR = 5; // 1~5, 6~10

export default function DesignConditionsForm() {
  // 5 segments for each direction, mirroring the yellow cells in the screenshot
  const [segmentsJinju, setSegmentsJinju] = useState(
    Array.from({ length: SEGMENT_COUNT_PER_DIR }, () => ({ ...EMPTY_SEGMENT }))
  );
  const [segmentsMasan, setSegmentsMasan] = useState(
    Array.from({ length: SEGMENT_COUNT_PER_DIR }, () => ({ ...EMPTY_SEGMENT }))
  );

  // Global design-speed / capacity inputs (also yellow-coded on the sheet)
  const [designInputs, setDesignInputs] = useState({
    designSpeedKmH: 80,    // 설계속도
    capacityUsageRatio: 0.3, // 설계 CODE row (0.3 / 0.2 / 0.1)
    serviceLevelCode: "D"    // 서비스수준 (A~F) – not yet used in calc
  });

  const handleSegmentChange = (dir, index, field, value) => {
    const setter = dir === "jinju" ? setSegmentsJinju : setSegmentsMasan;
    const current = dir === "jinju" ? segmentsJinju : segmentsMasan;
    const next = current.map((seg, i) =>
      i === index ? { ...seg, [field]: value } : seg
    );
    setter(next);
  };

  const handleDesignInputChange = (field, value) => {
    setDesignInputs((prev) => ({ ...prev, [field]: value }));
  };

  // --- Calculations ---

  const statsJinju = computeDirectionStats(segmentsJinju);
  const statsMasan = computeDirectionStats(segmentsMasan);

  const trafficJinju = estimateTrafficVolume({
    designSpeedKmH: designInputs.designSpeedKmH,
    capacityUsageRatio: designInputs.capacityUsageRatio,
    lanes: statsJinju.lanes
  });

  const trafficMasan = estimateTrafficVolume({
    designSpeedKmH: designInputs.designSpeedKmH,
    capacityUsageRatio: designInputs.capacityUsageRatio,
    lanes: statsMasan.lanes
  });

  return (
    <div style={{ padding: 20, fontFamily: "sans-serif" }}>
      <h1>Module 1 – Design Conditions & Estimated Traffic Volume</h1>

      {/* Design-speed / capacity controls */}
      <section style={cardStyle}>
        <h2>Global Design Inputs (yellow cells in bottom rows)</h2>
        <div style={grid2}>
          <label style={fieldLabel}>
            설계속도 V [km/h]
            <select
              value={designInputs.designSpeedKmH}
              onChange={(e) =>
                handleDesignInputChange("designSpeedKmH", Number(e.target.value))
              }
              style={selectStyle}
            >
              {Object.keys(SPEED_CAPACITY_TABLE).map((v) => (
                <option key={v} value={v}>
                  {v}
                </option>
              ))}
            </select>
          </label>

          <NumberInput
            label="용량 사용률 (Capacity usage ratio)"
            value={designInputs.capacityUsageRatio}
            onChange={(v) => handleDesignInputChange("capacityUsageRatio", v)}
          />

          <label style={fieldLabel}>
            서비스 수준 Code
            <select
              value={designInputs.serviceLevelCode}
              onChange={(e) =>
                handleDesignInputChange("serviceLevelCode", e.target.value)
              }
              style={selectStyle}
            >
              {["A", "B", "C", "D", "E", "F"].map((code) => (
                <option key={code} value={code}>
                  {code}
                </option>
              ))}
            </select>
          </label>
        </div>
        <p style={hintText}>
          Base capacity per lane at {designInputs.designSpeedKmH} km/h:{" "}
          {trafficJinju.baseCapacityPerLane} veh/h.
        </p>
      </section>

      {/* Segment inputs – Jinju direction */}
      <section style={cardStyle}>
        <h2>진주 방향 – Segments 1~5 (yellow cells)</h2>
        <SegmentsTable
          direction="jinju"
          segments={segmentsJinju}
          onChange={handleSegmentChange}
        />
        <SummaryRow
          label="진주 방향 합계"
          stats={statsJinju}
          traffic={trafficJinju}
        />
      </section>

      {/* Segment inputs – Masan direction */}
      <section style={cardStyle}>
        <h2>마산 방향 – Segments 6~10 (yellow cells)</h2>
        <SegmentsTable
          direction="masan"
          segments={segmentsMasan}
          onChange={handleSegmentChange}
        />
        <SummaryRow
          label="마산 방향 합계"
          stats={statsMasan}
          traffic={trafficMasan}
        />
      </section>
    </div>
  );
}

// ---------- small components ----------

function SegmentsTable({ direction, segments, onChange }) {
  return (
    <table style={tableStyle}>
      <thead>
        <tr>
          <th style={thStyle}>Seg #</th>
          <th style={thStyle}>터널구배 [%]</th>
          <th style={thStyle}>터널길이 [m]</th>
          <th style={thStyle}>평균표고 [m]</th>
          <th style={thStyle}>차선수 [N]</th>
        </tr>
      </thead>
      <tbody>
        {segments.map((seg, idx) => (
          <tr key={idx}>
            <td style={tdStyle}>{idx + 1}</td>
            <td style={tdStyle}>
              <NumberCellInput
                value={seg.slopePercent}
                onChange={(v) => onChange(direction, idx, "slopePercent", v)}
              />
            </td>
            <td style={tdStyle}>
              <NumberCellInput
                value={seg.lengthM}
                onChange={(v) => onChange(direction, idx, "lengthM", v)}
              />
            </td>
            <td style={tdStyle}>
              <NumberCellInput
                value={seg.avgElevationM}
                onChange={(v) => onChange(direction, idx, "avgElevationM", v)}
              />
            </td>
            <td style={tdStyle}>
              <NumberCellInput
                value={seg.laneCount}
                onChange={(v) => onChange(direction, idx, "laneCount", v)}
              />
            </td>
          </tr>
        ))}
      </tbody>
    </table>
  );
}

function SummaryRow({ label, stats, traffic }) {
  return (
    <div style={{ marginTop: 12, fontSize: 13 }}>
      <strong>{label}</strong> — 총 연장:{" "}
      {stats.totalLengthM.toFixed(2)} m, 차선수(최대): {stats.lanes}, 추정교통량:{" "}
      {traffic.estimatedVolume.toLocaleString()} 대/시간
    </div>
  );
}

function NumberInput({ label, value, onChange }) {
  return (
    <label style={fieldLabel}>
      {label}
      <input
        type="number"
        value={value}
        onChange={(e) => onChange(e.target.value)}
        style={inputStyle}
      />
    </label>
  );
}

function NumberCellInput({ value, onChange }) {
  return (
    <input
      type="number"
      value={value}
      onChange={(e) => onChange(e.target.value)}
      style={cellInputStyle}
    />
  );
}

// ---------- styles ----------

const cardStyle = {
  border: "1px solid #ddd",
  borderRadius: 8,
  padding: 16,
  marginBottom: 24
};

const grid2 = {
  display: "grid",
  gridTemplateColumns: "repeat(auto-fit, minmax(200px, 1fr))",
  gap: 12
};

const tableStyle = {
  width: "100%",
  borderCollapse: "collapse",
  marginTop: 8,
  fontSize: 12
};

const thStyle = {
  borderBottom: "1px solid #ccc",
